document.addEventListener("DOMContentLoaded",()=>{const tabsContainer=document.querySelector(".tabs");if(tabsContainer){tabsContainer.addEventListener("click",e=>{const{target}=e;if(target.matches(".tab-links a")){e.preventDefault();const currentAttrValue=target.getAttribute("href");const allTabs=document.querySelectorAll(".tabs .tab");allTabs.forEach(tab=>tab.style.display="none");const currentTab=document.querySelector(currentAttrValue);if(currentTab){currentTab.style.display="block"}const allTabLinks=document.querySelectorAll(".tabs .tab-links a");allTabLinks.forEach(link=>link.parentElement.classList.remove("active"));target.parentElement.classList.add("active")}})}});